    private void btnSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearchActionPerformed
        String en=txtEid.getText();
        
        CEmpDetails csc=new CEmpDetails();
        ResultSet rs=csc.getData(en);
      DefaultTableModel table=(DefaultTableModel)tblEmpDetail.getModel();
      table.setRowCount(0);
      try
      {
          while(rs.next())
          {
              table.addRow(new Object[]{rs.getString(1),rs.getString(2),rs.getString(3),rs.getString(4),rs.getString(5),rs.getString(6),rs.getString(7),rs.getString(8)});
          }
          rs.close();
      }
      catch(Exception e)
      {
          JOptionPane.showMessageDialog(null, e.getMessage());
      }
    }//GEN-LAST:event_btnSearchActionPerformed

    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown
      ResultSet rs=MSelect.getData("select * from tblEmployee");
      DefaultTableModel table=(DefaultTableModel)tblEmpDetail.getModel();
      table.setRowCount(0);
      try
      {
          while(rs.next())
          {
              table.addRow(new Object[]{rs.getString(1),rs.getString(2),rs.getString(3),rs.getString(4),rs.getString(5),rs.getString(6),rs.getString(7),rs.getString(8)});
          }
          rs.close();
      }
      catch(Exception e)
      {
          JOptionPane.showMessageDialog(null, e.getMessage());
      }
    }//GEN-LAST:event_formComponentShown

