    private void tblEmpDetailMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblEmpDetailMouseClicked
        int index=tblEmpDetail.getSelectedRow();
        TableModel model=tblEmpDetail.getModel();
        String email=model.getValueAt(index,2).toString();
           String status=model.getValueAt(index,7).toString();
           if(status.equals(true))
           {
               status="false";
           }
           else
           {
               status="true";
           }
           try
           {
           int a=JOptionPane.showConfirmDialog(null,"Do you want to change the status of '"+email+"'","Select",JOptionPane.YES_NO_OPTION);
           if(a==0)
           {
               MInsertUpdateDelete.setData("Update tblEmployee set status='"+status+"' where email='"+email+"'","Status changed succesfully");
               setVisible(false);
               new VEmpDetails().setVisible(true);
           }
           }
           catch(Exception e)
           {
             JOptionPane.showMessageDialog(null,e.getMessage());
           }
    }//GEN-LAST:event_tblEmpDetailMouseClicked

